<?php
// don't load directly 
if ( !defined('ABSPATH') ) {
	header( 'Status: 403 Forbidden' );
	header( 'HTTP/1.1 403 Forbidden' );
	exit();
}
//error_reporting( E_CORE_ERROR | E_CORE_WARNING | E_COMPILE_ERROR | E_ERROR | E_WARNING | E_PARSE | E_USER_ERROR | E_USER_WARNING | E_RECOVERABLE_ERROR );

$cached = get_transient( '_etruel_wpem_reviews_data' );
// Always return cached transients if exists.
if ( !empty( $cached ) ) {
	echo $cached;
	return;
}

$url = 'https://wordpress.org/support/view/plugin-reviews/wpematico?filter=5';

$ch = curl_init($url);
$cookie_jar = tempnam('./tmp','cookie');
curl_setopt($ch, CURLOPT_URL, $url);
curl_setopt($ch, CURLOPT_VERBOSE, true);
curl_setopt($ch, CURLOPT_USERAGENT, "Mozilla/5.0 (Windows; U; Windows NT 5.1; rv:1.7.3) Gecko/20041001 Firefox/0.10.1" );
curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
curl_setopt($ch, CURLOPT_COOKIEJAR, $cookie_jar);
$html = curl_exec($ch);
//print_r( curl_getinfo ( $ch ),1 ); //exit;
// remove the cookie jar
unlink($cookie_jar) /*or die("Can't unlink $cookie_jar")*/;

if (!$html) {
	echo '<div id="reviews">'.  __('Can\t connect to Wordpress Site', WPeMatico::TEXTDOMAIN ).'</div>';
	return false;
}

// EMPIEZA STRPOS
$tiempo = microtime(true);
$reviews = substr($html, strpos($html, '<div class="all-reviews">') );
$reviews = substr($reviews, 0, strpos($reviews, '<div class="topicnav">')  );
$reviews = str_replace('<span class="review-date"', '<br><span class="review-date"', $reviews );
$reviews = str_replace('<a ', '<a target="_Blank" ', $reviews );
//echo ' '. (string)(microtime(true)-$tiempo) . ' milisec STRPOS<br>';
